import moment from 'moment';
import { scrollToBottom } from "../../helpers/scroll.js";
import MessagesList from "./messages/MessagesList.vue";
import InputContainer from "./input/InputContainer.vue";
import axios from 'axios';
export default {
  name: 'BasicVueChat',
  components: {
    MessagesList: MessagesList,
    InputContainer: InputContainer
  },
  props: {
    title: {
      type: String,
      default: 'Chat',
      required: false
    },
    initialAuthorId: {
      type: Number,
      default: 0,
      required: false
    },
    attachMock: {
      type: Boolean,
      default: false,
      required: false
    },
    initialFeed: {
      type: Array,
      default: function _default() {
        return [];
      },
      required: false
    },
    newMessage: {
      type: Object,
      default: function _default() {
        return {};
      },
      required: false
    }
  },
  data: function data() {
    return {
      feed: [],
      authorId: this.$store.getters.getUser.id,
      toggleEmojiPicker: false,
      to: ''
    };
  },
  watch: {
    newMessage: function newMessage(newValue, oldValue) {
      console.log('scrollbottom');
      this.pushToFeed(newValue);
      scrollToBottom();
    }
  },
  mounted: function mounted() {
    var _this = this;

    if (this.attachMock) {
      import("./mocks/mock-messages-list.js").then(function (mockData) {
        _this.feed = mockData.default.feed;

        _this.setAuthorId(mockData.default.authorId);
      }).catch(function (error) {
        console.error('Failed to load mock data from file. ', error);
      });
    } else {
      this.feed = this.initialFeed;
    }

    console.log('BasicChat::::', this.title);
    this.recvMsg();
    this.setRoom(this.title); //Set when the window is opened later

    this.$store.state.bus.$on('openDial', function () {
      console.log('receive openDial!!!::::', _this.title);

      _this.setRoom(_this.title);
    });
  },
  created: function created() {//create Component
    //enroll Callback receiving msg from server
  },
  methods: {
    setRoom: function setRoom(to) {
      var _this2 = this;

      //to is partner
      console.log('BasicChat.vue:::call setRoom');
      axios.post('http://localhost:3000/apis/db/getChat', {
        srcId: this.$store.getters.getUser.id,
        to: to
      }).then(function (r) {
        _this2.feed = r.data.convs;
        scrollToBottom();
      }).catch(function (e) {
        return console.error(e.message);
      });
    },
    recvMsg: function recvMsg() {
      var _this3 = this;

      console.log('call recvMsg!!!!!');
      this.$store.state.socks.sock.on('msg', function (data) {
        //Data received is processed at here
        if (data.header.to === _this3.$store.getters.getUser.position && _this3.$store.getters.getRoom.indexOf(data.header.from) != -1) {
          _this3.onNewOwnMessage(data.msg);
        }
      }); //send work

      var eve = 'work';

      if (this.$store.getters.getMountedCheck.indexOf(eve) == -1) {
        this.$store.commit('pushMountedCheck', eve);
        this.$store.state.bus.$on(eve, function (data) {
          /* process when multiple receiver exist */

          /* code here */
          //수신자 배열을 루프돌아서 동일한 work 메시지를 전송시킨다.
          console.log('selects in BasicChat.vue::::', data);
          var selects = data.selects;
          delete data['selects'];
          console.log('check delete ::: BasicChat.vue :::', data);
          var selDivisions = [];
          var others = _this3.$store.getters.getOthers;
          selects.forEach(function (ele) {
            //보내는 대상이 부서면
            if (_this3.$store.getters.getDivisions.indexOf(ele) != -1) {
              //전체 유저 검색
              for (var i = 0; i < others.length; i++) {
                if (others[i].division == ele) {
                  _this3.setInput('', '', '', data, others[i].position);
                }
              } //나중에 또 안보내기 위해서 보낸 부서는 저장


              selDivisions.push(ele);
            }
          }); //부서 설정이 아닌 사람 대상인 경우에 대해 보내기

          selects.forEach(function (ele) {
            if (selDivisions.indexOf(ele) != -1) {} else {
              _this3.setInput('', '', '', data, ele);
            }
          });
        });
      }
    },
    setInput: function setInput(message, image, imageUrl, works, to) {
      var _this4 = this;

      var msg = {
        id: this.authorId,
        position: this.$store.getters.getUser.position,
        contents: message,
        image: image,
        imageUrl: imageUrl,
        date: moment().format('HH:mm:ss'),
        works: works
      };

      if (works != undefined) {
        console.log('set flag_date in BasicChat::::');
        works.flag_date = msg.date;
      }

      if (to === undefined) {
        to = this.title;
      }

      var header = {
        to: to,
        from: this.$store.getters.getUser.position
      };
      this.$store.state.socks.sock.emit('msg', {
        msg: msg,
        header: header
      });

      if (works != undefined) {
        setTimeout(function () {
          _this4.$store.state.socks.sock.emit('sock_initWorks', {
            id: _this4.$store.getters.getUser.id
          });

          _this4.$store.commit('initWorks');
        }, 50);
      }

      console.log('이사람에게 보냈어::::', to);

      if (to === this.title) {
        this.onNewOwnMessage(msg);
        console.log('TTTTAAAA');
      }
    },
    setEmojiPickerToggle: function setEmojiPickerToggle(toggle) {
      this.toggleEmojiPicker = toggle;
    },
    pushToFeed: function pushToFeed(element) {
      this.feed.push(element);
      setTimeout(function () {
        scrollToBottom();
      }, 100);
    },
    onNewOwnMessage: function onNewOwnMessage(data) {
      this.pushToFeed(data);
    },
    onOpenEmojiPicker: function onOpenEmojiPicker(toggle) {
      this.setEmojiPickerToggle(toggle);
    }
  }
};
