<template>
  <div
    class="basic-vue-chat"
    @click="setEmojiPickerToggle(false)">
    <section class="window">
      <header class="window__header__container">
        <slot name="header">
          {{ title }}
        </slot>
      </header>
      <section
        id="window__messages__container"
        class="window__messages__container">
        <slot>
          <messages-list
            :feed="feed"
            :author-id="authorId"
            class="messages-list" />
        </slot>
      </section>
      <div class="window__input__container">
        <slot name="input-container">
          <input-container
            :sel="title"
            :toggle-emoji-picker="toggleEmojiPicker"
            @newOwnMessage="setInput"
            @openEmojiPicker="onOpenEmojiPicker"
          />
        </slot>
      </div>
    </section>
  </div>
</template>

<script>
import moment from 'moment'
import { scrollToBottom } from '../../helpers/scroll.js'
import MessagesList from './messages/MessagesList.vue'
import InputContainer from './input/InputContainer.vue'
import axios from 'axios'
export default {
  name: 'BasicVueChat',
  components: {
    MessagesList,
    InputContainer,
  },
  props: {
    title: {
      type: String,
      default: 'Chat',
      required: false
    },
    initialAuthorId: {
      type: Number,
      default: 0,
      required: false
    },
    attachMock: {
      type: Boolean,
      default: false,
      required: false
    },
    initialFeed: {
      type: Array,
      default: function () {
        return []
      },
      required: false
    },
    newMessage: {
      type: Object,
      default: function () {
        return {}
      },
      required: false
    }
  },
  data: function () {
    return {
      feed: [],
      authorId : this.$store.getters.getUser.id,
      toggleEmojiPicker: false,
      to : ''
    }
  },
  watch: {
    newMessage: function (newValue, oldValue) {
      console.log('scrollbottom')
      this.pushToFeed(newValue)
      scrollToBottom()
    }
  },
  mounted () {
    if (this.attachMock) {
      import('./mocks/mock-messages-list.js')
        .then(mockData => {
          this.feed = mockData.default.feed
          this.setAuthorId(mockData.default.authorId)
        })
        .catch(error => {
          console.error('Failed to load mock data from file. ', error)
        })
    } else {
      this.feed = this.initialFeed
    }
    console.log('BasicChat::::', this.title)
    this.recvMsg()
    this.setRoom(this.title)

    //Set when the window is opened later
    this.$store.state.bus.$on('openDial', ()=>{
    console.log('receive openDial!!!::::', this.title)
    this.setRoom(this.title)
    })
  },
  created() {
    //create Component


    //enroll Callback receiving msg from server
  },
  methods: {
    setRoom(to){
      //to is partner
      console.log('BasicChat.vue:::call setRoom')
      axios.post('http://webhacker.xyz:8000/apis/db/getChat', {srcId: this.$store.getters.getUser.id, to: to})
      .then(r =>{
        this.feed = r.data.convs
        scrollToBottom()
      })
      .catch(e => console.error(e.message))
    },
    recvMsg(){
      this.$store.state.socks.sock.on('msg', data =>{
        //Data received is processed at here

        if((data.header.to === this.$store.getters.getUser.position) && (this.$store.getters.getRoom.indexOf(data.header.from) != -1)){
            this.onNewOwnMessage(data.msg)
        }
      })
      //send work
      this.$store.state.bus.$on('work', (data) => {
        /* process when multiple receiver exist */

        /* code here */
        //수신자 배열을 루프돌아서 동일한 work 메시지를 전송시킨다.

        var works = {
          title: data.title,
          contents: data.contents,
          startDate: data.startDate,
          endDate: data.endDate,
          file_save: data.file_save,
          file_real: data.file_real,
          state_work: data.state_work,
          state_notice: data.state_notice,
          notice: data.notice === 'true',
        }
        var selDivisions = []
        var others = this.$store.getters.getOthers
        data.selects.forEach(ele=>{
          //보내는 대상이 부서면
          if(this.$store.getters.getDivisions.indexOf(ele) != -1){
            //전체 유저 검색
              for(var i = 0; i<others.length; i++){
                if(others[i].division == ele){

                console.log('selects::::', ele)
                  this.setInput('', '', '', works, others[i].position)
                }
              }
                //나중에 또 안보내기 위해서 보낸 부서는 저장
              selDivisions.push(ele)
            }
        })
        //부서 설정이 아닌 사람 대상인 경우에 대해 보내기
        data.selects.forEach(ele=>{
            if(selDivisions.indexOf(ele) != -1){
              console.log('메시지가 이미 보내질 것이야')
            }else{
              console.log('selDivisions::::', selDivisions)
              console.log('others::::', ele)
              this.setInput('', '', '', works, ele)
            }
        })
        

      })
    },
    setInput(message, image, imageUrl, works, to){
      const msg = {
        id: this.authorId,
        position: this.$store.getters.getUser.position,
        contents: message,
        image: image,
        imageUrl: imageUrl,
        date: moment().format('HH:mm:ss'),
        works: works
      }
      console.log('send msg::::', msg.works)
      console.log('send msg::::', works)
      if(to === undefined){
        to = this.title
      }
      const header = {
        to : to,
        from : this.$store.getters.getUser.position
      }
      this.$store.state.socks.sock.emit('msg', {msg: msg, header: header})

      if(to === this.title)
        this.onNewOwnMessage(msg)
    },
    setEmojiPickerToggle (toggle) {
      this.toggleEmojiPicker = toggle
    },
    pushToFeed (element) {
      this.feed.push(element)
      scrollToBottom()
    },
    onNewOwnMessage (data) {
      this.pushToFeed(data)
    },
    onOpenEmojiPicker (toggle) {
      this.setEmojiPickerToggle(toggle)
    }
  }
}
</script>

<style lang="scss">
@import "../../assets/scss/main.scss";
</style>
