<template>
  <div
    class="basic-vue-chat"
    @click="setEmojiPickerToggle(false)">
    <section class="window">
      <header class="window__header__container">
        <slot name="header">
          {{ title }}
        </slot>
      </header>
      <section
        id="window__messages__container"
        class="window__messages__container">
        <slot>
          <messages-list
            :feed="feed"
            :author-id="authorId"
            class="messages-list" />
        </slot>
      </section>
      <div class="window__input__container">
        <slot name="input-container">
          <input-container
            :toggle-emoji-picker="toggleEmojiPicker"
            @newOwnMessage="setInput"
            @openEmojiPicker="onOpenEmojiPicker"
          />
        </slot>
      </div>
    </section>
  </div>
</template>

<script>
import moment from 'moment'
import { scrollToBottom } from '../../helpers/scroll.js'
import MessagesList from './messages/MessagesList.vue'
import InputContainer from './input/InputContainer.vue'
import axios from 'axios'
export default {
  name: 'BasicVueChat',
  components: {
    MessagesList,
    InputContainer
  },
  props: {
    title: {
      type: String,
      default: 'Chat',
      required: false
    },
    initialAuthorId: {
      type: Number,
      default: 0,
      required: false
    },
    attachMock: {
      type: Boolean,
      default: false,
      required: false
    },
    initialFeed: {
      type: Array,
      default: function () {
        return []
      },
      required: false
    },
    newMessage: {
      type: Object,
      default: function () {
        return {}
      },
      required: false
    }
  },
  data: function () {
    return {
      feed: [],
      authorId : this.$store.getters.getUser.id,
      toggleEmojiPicker: false,
      to : ''
    }
  },
  watch: {
    newMessage: function (newValue, oldValue) {
      this.pushToFeed(newValue)
      scrollToBottom()
    }
  },
  mounted () {
    if (this.attachMock) {
      import('./mocks/mock-messages-list.js')
        .then(mockData => {
          this.feed = mockData.default.feed
          this.setAuthorId(mockData.default.authorId)
        })
        .catch(error => {
          console.error('Failed to load mock data from file. ', error)
        })
    } else {
      this.feed = this.initialFeed
    }
    this.recvMsg()
    this.setRoom(this.to)
    //Set when the window is opened later
    this.$store.state.bus.$on('openDial', (to)=>{
    this.setRoom(to)
    this.title = to
    })
  },
  created() {
    //create Component


    //enroll Callback receiving msg from server
  },
  methods: {
    setRoom(to){
      //to is partner
      console.log('BasicChat.vue:::call setRoom')
      axios.post('http://webhacker.xyz:8000/apis/db/getChat', {srcId: this.$store.getters.getUser.id, to: to})
      .then(r =>{
        console.log('getChat:::: ', r)
        this.feed = r.data.convs
      })
      .catch(e => console.error(e.message))
    },
    recvMsg(){
      this.$store.state.socks.sock.on('msg', data =>{
        //Data received is processed at here

        if((data.header.to === this.$store.getters.getUser.position) && (this.$store.getters.getRoom.indexOf(data.header.from) != -1)){
            this.onNewOwnMessage(data.msg)
        }
      })
    },
    setInput(message, image, imageUrl, works){
      const msg = {
        id: this.authorId,
        position: this.$store.getters.getUser.position,
        contents: message,
        image: image,
        imageUrl: imageUrl,
        date: moment().format('HH:mm:ss'),
        works: works
      }
      const header = {
        to : this.title,
        from : this.$store.getters.getUser.position
      }
      this.$store.state.socks.sock.emit('msg', {msg: msg, header: header})
      this.onNewOwnMessage(msg)
    },
    setEmojiPickerToggle (toggle) {
      this.toggleEmojiPicker = toggle
    },
    pushToFeed (element) {
      this.feed.push(element)
    },
    onNewOwnMessage (data) {
      this.pushToFeed(data)

      scrollToBottom()
    },
    onOpenEmojiPicker (toggle) {
      this.setEmojiPickerToggle(toggle)
    }
  }
}
</script>

<style lang="scss">
@import "../../assets/scss/main.scss";
</style>
