import Header from './Header.vue'
import MessageDisplay from './MessageDisplay.vue'
import MessageManager from './MessageManager.vue'
import { mapMutations } from 'vuex';
import store from '../store'
export default {
    name: 'Chat',
    store,
    components:{
        Header,
        MessageDisplay,
        MessageManager
    },
    props: {
        participants: {
            type: Array,
            required: true
        },
        messages: {
            type: Array,
            required: true
        },
        myself: {
            type: Object,
            required: true
        },
        chatTitle:{
            type: String,
            required: false,
            default: ''
        },
        placeholder: {
            type: String,
            required: false,
            default: 'type your message here'
        },
        onType: {
            type: Function,
            required: false,
            default: () => false
        },
        onMessageSubmit: {
            type: Function,
            required: false,
            default: () => false
        },
        colors: {
            type: Object,
            required: true
        },
    },
    methods: {
        ...mapMutations([
            'setParticipants',
            'setMyself',
            'setMessages',
            'setPlaceholder',
            'setChatTitle'
        ])
    },
    mounted(){
        this.setParticipants(this.participants);
        this.setMyself(this.myself);
        this.setMessages(this.messages);
        this.setPlaceholder(this.placeholder);
        this.setChatTitle(this.chatTitle);
    }
}
